mapper.select:
select count(*)
from user_cart
where
product_serial_num=#{product_serial_num}
and
user_serial_num=#{user_serial_num}

mapper.delete:
delete from user_cart
where
user_serial_num = #{user_serial_num}
and
product_serial_num =
#{product_serial_num}

mapper.update:
UPDATE funding_history
SET
user_funding_amount = user_funding_amount + #{payMoney},
user_funding_pct = user_funding_pct + #{pay}
WHERE
user_serial_num =
#{user_serial_num}
AND product_serial_num = #{product_serial_num}
