mapper.delete:
delete from user_cart
where 
	user_serial_num = #{user_serial_num}
and 
	product_serial_num = #{product_serial_num}

mapper.select:
select count(*)
from user_cart
where 
	product_serial_num=#{product_serial_num}
and
	user_serial_num=#{user_serial_num}

select count(*) 
from users
where user_serial_num = #{user_serial_num}
and user_point >= #{user_point}

select user_email, user_name
from users
where user_serial_num = #{user_serial_num}
